package gcg

var scalarsTemplateSrc = `package {{.Package}}
// generated by github.com/emicklei/graphql-client-gen/cmd/gcg version: {{.BuildVersion }}
// DO NOT EDIT

// These aliases are needed to model return types in Data fields
type String string
type Int int32
type Float float64
type Boolean bool
{{- if .IncludeScalarID}}
// ID can by any type, typically a string or int64
type ID interface{}
{{- end }}

{{- range .Scalars}}

// {{.Name}} is a Scalar. {{.Comment}}
type {{.Name}} string

// New{{.Name}} returns a pointer to a {{.Name}} value. 
// Use type conversion instead e.g. v := {{.Name}}(s) if you need the non-pointer value.
func New{{.Name}}(s string) *{{.Name}} { v := {{.Name}}(s); return &v }

{{- end}}
`
